import React from "react";
import ReactDOM from "react-dom";
import OrderHeading from "./OrderHeading";
import Details from "./Details";
import PackageFilter from "./PackageFilter";
import Footer from "./Footer";
import "./App.css";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      work_orders: [],
      worker: "",
      work_by_order: 0,
      search: "",
      isFetching: true,
      // showDetail: false
    };
fetch("/json/work_orders.json")
      .then((res) => res.json())
      .then((data) => {
        let dataArray = [data];
        dataArray[0].orders.sort(
          (a, b) => a.deadline - b.deadline
        );
        this.setState({ work_orders: dataArray[0] , showDetail:false});
      })
      .then(() => stateUpdate2());

    let stateUpdate2 = () => {
      fetch("/json/worker.json")
        .then((res) => res.json())
        .then((data) => this.setState({ worker: data, isFetching: false }));
    };
  }
  updateSearch = (event) => {   
    this.setState({ search: event.target.value });
    // this.state.work_orders?.map((link) =>
    // console.log("loggg****"+ link) 
    // );
    //console.log("*********" + listItems);
    /*console.log(event.target.value.substring(0, 30), "this is my input"); 
    if((this.state.search)== (event.target.value)){      
      this.setState({ showDetail: true });
    }else{
      this.setState({ showDetail: false });
    }
    console.log(this.state.search, "this is my search");
    console.log(this.state.work_orders, "***********this is my search");*/
  };

  render() {
    //console.log(this.state.work_orders.orders, "These are my orders");
    return (
      <React.Fragment>
        <div className="stickyFooterWrapper">
          {this.state.isFetching ? (
            <div id="loadingScreen">
              <p>Loading...</p>
            </div>
          ) : (
            <React.Fragment>
              <OrderHeading />
              <section id="controls">
              <PackageFilter
                  onChange={this.updateSearch.bind(this)}
                  value={this.state.search}
                /> 
              </section>
                {/* {this.state.showDetail? ( */}
                  <section id="ordersContainer">
                    {this.state.work_orders.orders.map((orders) => (
                      <OrderTiles
                        key={orders.id}
                        data-workername={
                          this.state.worker[orders.workerId].worker.name
                        }
                        orderId={orders.id}
                        workOrderName={orders.name}
                        workOrderDescription={orders.description}
                        workOrderAmount={orders.amount}
                        workOrderworkerId={orders.workerId}
                        employeeImage={
                          this.state.worker[orders.workerId].worker.image
                        }
                        employeeName={
                          this.state.worker[orders.workerId].worker.name
                        }
                        employeecontact={
                          this.state.worker[orders.workerId].worker.contact
                        }
                        employeeEmail={
                          this.state.worker[orders.workerId].worker.email
                        }
                        deadline={orders.deadline}
                        search={this.state.search}
                      />
                    ))}
                  </section>               
                   {/* ):(
                   <div id="initailPage">
                         <p>Search for your order above to get the details......</p>
                   </div>
                 )} */}
            </React.Fragment>
          )}
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}
ReactDOM.render(<App />, document.getElementById("root"));
