import React from "react";
import ReactDOM from "react-dom";
import OrderHeading from "./OrderHeading";
import OrderTiles from "./OrderTiles";
import PackageFilter from "./PackageFilter";
import Footer from "./Footer";
import "./App.css";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      work_orders: [],
      filter_workOrder:"Rama",
      worker: "",
      work_by_order: 0,
      search: "",
      isFetching: true
    };

    // Start of state being called from json files using fetch to simulate if I did not have files on hand.
    // fetch requests made here for all the data needed for the page, a loading screen will be shown until all the data loading has been dealt with sequentially thanks to promises
    // isFetching is lastely changed so that page is all loaded so there won't be any errors with state not being loaded yet.

    fetch("/json/work_orders.json")
      .then((res) => res.json())
      .then((data) => {
        let dataArray = [data];
        dataArray[0].orders.sort(
          (a, b) => a.deadline - b.deadline
        );
        this.setState({ work_orders: dataArray[0] });
      })
      .then(() => stateUpdate2());

    let stateUpdate2 = () => {
      fetch("/json/worker.json")
        .then((res) => res.json())
        .then((data) => this.setState({ worker: data, isFetching: false }));
    };
  }
  updateSearch = (event) => {
    this.setState({ search: event.target.value });
  };

  resultSearch = (event) => {

    let result = this.state.work_orders.orders.filter((workOrder)=>{
      if(workOrder.id ===event.target.value){
        return workOrder
      }
    })
    console.log( result[0],"This is my result ")
    console.log( result[0].name,"This is my result name")
    console.log( this.state.search,"This is my Searchhhhhh")
    this.setState({filter_workOrder:"Krishn"})
    //console.log(this.state.work_orders.orders, "work ordersds!!!!");
    console.log(this.state.filter_workOrder, "Filter****************");
  };


  render() {
    return (
      <React.Fragment>
        <div className="stickyFooterWrapper">
          {this.state.isFetching ? (
            <div id="loadingScreen">
              <p>Loading...</p>
            </div>
          ) : (
            <React.Fragment>
              <OrderHeading />
              <section id="controls">
                <PackageFilter
                  //onClick={this.resultSearch.bind(this)}
                  value={this.state.search}
                  onChange={this.updateSearch.bind(this)}
                />
                <button type="submit" id="searchSubmit"  onClick={this.resultSearch(this.state.search).bind(this)}>Submit</button>
              </section>
              <section id="ordersContainer">
                {this.state.work_orders.orders.map((orders) => (
                  <OrderTiles
                    key={orders.id}
                    data-workername={
                      this.state.worker[orders.workerId].worker.name
                    }
                    orderId={orders.id}
                    workOrderName={orders.name}
                    workOrderDescription={orders.description}
                    workOrderAmount={orders.amount}
                    workOrderworkerId={orders.workerId}
                    employeeImage={
                      this.state.worker[orders.workerId].worker.image
                    }
                    employeeName={
                      this.state.worker[orders.workerId].worker.name
                    }
                    employeecontact={
                      this.state.worker[orders.workerId].worker.contact
                    }
                    employeeEmail={
                      this.state.worker[orders.workerId].worker.email
                    }
                    deadline={orders.deadline}
                    search={this.state.search}
                  />
                ))}
              </section> 
            </React.Fragment>
          )}
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

// Render to index.html

ReactDOM.render(<App />, document.getElementById("root"));
